mapper.select:
SELECT id, username, email, status, created_at, updated_at
FROM users


        AND status = #{status}


        AND username LIKE CONCAT('%', #{username}, '%')


ORDER BY created_at DESC

    LIMIT #{limit}


    OFFSET #{offset}

SELECT id, username, email, status, created_at, updated_at
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users
username = #{username}, email = #{email}, status = #{status}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
